    let count = collection_type.filter(a =&gt; a == c).length;
    if (c.includes("No report")) c = "Established Network Repository";
    if (!c.includes("Lost")) continue;
    d.push({"collection_type": c, "count": count})
  }

  return Plot.plot({
    color: {
      scheme: "PiYg" 
    },
    x: {
      domain: [0, 15]
    },
